"use strict";
var helpers = require('syracuse-core/lib/helpers');
var ModuleList = require("./moduleList").ModuleList;
var MenuItemRecord = require('./menuItemRecord').MenuItemRecord;

function NavigationItemList() {}

exports.NavigationItemList = helpers.defineClass(NavigationItemList, ModuleList, {
	createBuilder: function() {
		this.RecordClass = MenuItemRecord;
		this.$item.$isTopLabelAlignment = true;
		if (!this.$item.$layout) {
			this.$item.$layout = {};
		}
		var $items = this.$item.$layout.$items;
		if (!$items) {
			$items = this.$item.$layout.$items = [];
			var $binds = Object.keys(this.$fields);
			for (var ii = 0, jj = $binds.length; ii < jj; ii++) {
				if (!this.$fields[$binds[ii]].$isExcluded) {
					$items.push({
						$bind: $binds[ii]
					});
				}
			}
		}
	},
	loadSelector: function() {
		this.selector = {};
	}
});