var SendBag = require('./delta/sendBag').SendBag;
var DeltaManager = require('./delta/deltaManager').DeltaManager;
var LayoutValidator = require("syracuse-ui/lib/article/layouts/layoutValidator").LayoutValidator;

var _$mainActions = {
	$cube: "$create",
	$query: "$create",
	$details: "$edit",
	$edit: "$save",
	$request: "$invoke"
};
