function _getFieldSection(localize) {
	return {
		$category: "section",
		$title: localize.formula_blck_fields,
		$isBoxCollapsable: true,
		$layout: {
			$items: [{
				$selectMode: "row",
				$bind: "fl",
				$isEditMode: false,
				$isDockedHeight: true,
				$isMenuRecordHidden: true,
				$isPagerHidden: false,
				$isPopupMenuHidden: true,
				$isQuickDesignerDisabled: true,
				$isTopLabelAlignment: true,
				$layout: {
					$items: [{
						$bind: "fl1"
					}, {
						$bind: "fl2"
					}, {
						$bind: "fl3"
					}, {
						$bind: "fl4"
					}]
				}

			}]
		}
	};
}

