exports.$prototype = {
    "$properties": {
        "$isHidden": {
            "$type": "application/x-boolean",
            "$title": "{@isHidden}"
        },
        "$isTitleHidden": {
            "$type": "application/x-boolean",
            "$title": "{@isLabelHidden}"
        },
        "$isTopLabelAlignment": {
            "$type": "application/x-choice",
            "$value": {
                "$type": "application/x-string",
                "$enum": [{
                    "$value": false,
                    "$title": "{@labelLeftAlignment}"
                }, {
                    "$value": true,
                    "$title": "{@labelTopAlignment}"
                }]
            }
        },
        "$isRightTextLabelAlignment": {
            "$type": "application/x-choice",
            "$title": "{@textLabelAlignment}",
            "$value": {
                "$type": "application/x-string",
                "$enum": [{
                    "$value": false,
                    "$title": "{@textLabelLeftAligned}"
                }, {
                    "$value": true,
                    "$title": "{@textLabelRightAligned}"
                }]
            },
            "$isDisabled": true
        }
    },
    "$links": {
        "$switchMode": {
            "$title": "{@showLightMode}"
        },
        "$addSection": {
            "$title": "{@addSection}"
        },
        "$addBlock": {
            "$title": "{@addBlock}"
        },
        "$addField": {
            "$title": "{@addField}"
        }
    },
    $localization: {
        "@sectionAppearance": "Appearance",
        "@blockPosition": "Labels position",
        "@textLabelAlignment": "text alignment",
        "@isHidden": "hide me",
        "@isLabelHidden": "hide label",
        "@textLabelLeftAligned": "left",
        "@textLabelRightAligned": "right",
        "@labelLeftAlignment": "at left",
        "@labelTopAlignment": "at top",
        "@showLightMode": "show light mode",
        "@addSection": "add a section",
        "@addBlock": "add a block",
        "@addField": "add a field"
    }
};
exports.data = {
    "$isTitleHidden": false,
    "$isTopLabelAlignment": true,
    "$isRightTextLabelAlignment": false
};
exports.$item = {
    "$category": "section",
    "$skin": "s-aw-designer-item",
    "$layout": {
        "$layoutType": "row",
        "$fitToContent": true,
        "$items": [{
            "$category": "section",
            "$title": "{@sectionAppearance}",
            "$layout": {
                "$layoutType": "row",
                "$fitToContent": true,
                "$items": [{
                    "$layoutType": "stack",
                    "$items": [{
                        "$bind": "$isHidden",
                        "$skin": "s-aw-field",
                        "$isEditMode": true,
                        "$isLeftSpaceHidden": true
                    }, {
                        "$bind": "$isTitleHidden",
                        "$skin": "s-aw-field",
                        "$isEditMode": true,
                        "$isLeftSpaceHidden": true
                    }]
                }, {
                    "$category": "block",
                    "$title": "{@blockPosition}",
                    "$layout": {
                        "$items": [{
                            "$bind": "$isTopLabelAlignment",
                            "$skin": "s-aw-field",
                            "$isEditMode": true,
                            "$isLeftSpaceHidden": true
                        }, {
                            "$bind": "$isRightTextLabelAlignment",
                            "$skin": "s-aw-field",
                            "$isEditMode": true,
                            "$isTopLabelAlignment": false,
                            "$labelWidth": "auto"
                        }]
                    }
                }]
            }
        }, {
            "$category": "block",
            "$layout": {
                "$items": []
            }
        }]
    }
};
