                            "$type": "application/json",
                            "$prototype": "{$baseUrl}/$prototypes('{$representation}.$search')",
                            "$url": "{$baseUrl}/{$pluralType}('{$key}')",
                            "$value": "{$key}",
                            "$title": "queryResult {$key}",
                            "$key": "{$uuid}",
                            "$description": "queryResult {$key}",
