"use strict";

exports.getSearchModeLinks = function() {
	return {
		$searchStartingWith: {
			$title: "Results starting with '{$search}'",
			$url: "/sdata/syracuse/search/syracuse/$search?representation=queryResult.$search&q={$search}"
		},
		$searchContaining: {
			$title: "Results containing '{$search}'",
			$url: "/sdata/syracuse/search/syracuse/$xsearch?representation=queryResult.$search&q={$search}"
		},
		$searchSimilar: {
			$title: "Results similar to '{$search}'",
			$url: "/sdata/syracuse/search/syracuse/$fsearch?representation=queryResult.$search&q={$search}"
		}
	};
};


exports.getInitSearchMode = function() {
	return "$searchStartingWith";
};