"use strict";

exports.getSearchModeLinks = function() {
	return {
		$searchStartingWith: {
			$title: document.site.localize.searchStartingWith,
			$url: "/sdata/syracuse/search/syracuse/$search?representation=queryResult.$search&q={$search}"
		},
		$searchSimilar: {
			$title: document.site.localize.searchSimilar,
			$url: "/sdata/syracuse/search/syracuse/$search?representation=queryResult.$search&q={$search}&qt=fuzzy"
		}
	};
};


exports.getInitSearchMode = function() {
	return "$searchStartingWith";
};