"use strict";
var helpers = require('syracuse-core/lib/helpers');

function ResizeListener() {}

exports.ResizeListener = helpers.defineClass(ResizeListener, null, {
	load: function() {
		this._observers = {};
	},
	add: function(observer) {
		this._observers[observer.id] = observer;
	},
	remove: function(observer) {
		if (this._observers) {
			delete this._observers[observer.id];
		}
	},
	notifyObservers: function(onBoxToggle) {
		if (this._observers) {
			var ids = Object.keys(this._observers);
			for (var ii = 0, jj = ids.length; ii < jj; ii++) {
				this._observers[ids[ii]].onWindowResize(onBoxToggle);
			}
		}
	},
	dispose: function() {
		delete this._observers;
	}
});