"use strict";

var _sdataHost = (window.location.protocol.toLowerCase() === "file:") ? "" : (window.location.protocol + "//" + window.location.host);
exports.logOnServer = function(msg, sync) {
	$.ajax({
		type: "GET",
		async: (sync !== true),
		url: _sdataHost + "/sdata/syracuse/collaboration/syracuse/clientLogs/$service/log?text=" + encodeURIComponent(msg),
		success: function(xhr) {}
	});
	//console.log(msg) ;
};