exports.settingBar = {
    "$layoutType": "tabs",
    "$items": [{
        "$category": "section",
        "$title": "Settings",
        "$layout": {
            "$items": [{
                "$layoutType": "stack",
                "$items": [{
                    "$layoutType": "row",
                    "$items": [{
                        "$skin": "s-record-menus-link",
                        "$category": "link",
                        "$bind": "$empty"
                    }, {
                        "$skin": "s-record-menus-link",
                        "$category": "link",
                        "$bind": "$refresh"
                    }]
                }, {
                    "$layoutType": "row",
                    "$items": [{
                        "$bind": "$fieldRecordCount",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$fixedBodyHeight",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$fitContainer",
                        "$isEditMode": true
                    }]
                }, {
                    "$layoutType": "row",
                    "$items": [{
                        "$bind": "$field$editMode",
                        "$format": "$combo",
                        "$isEditMode": true
                    }]
                }, {
                    "$category": "block",
                    "$title": "$styles",
                    "$layout": {
                        "$items": [{
                            "$layoutType": "row",
                            "$items": [{
                                "$skin": "s-record-menus-link",
                                "$category": "link",
                                "$bind": "$applyStyleToArray"
                            }, {
                                "$skin": "s-record-menus-link",
                                "$category": "link",
                                "$bind": "$applyStyleToRecord"
                            }, {
                                "$skin": "s-record-menus-link",
                                "$category": "link",
                                "$bind": "$applyStyleToField"
                            }]
                        }, {
                            "$bind": "$field$fieldStyle",
                            "$choiceLayout": 4,
                            "$format": "$combo",
                            "$isEditMode": true
                        }, {
                            "$bind": "$field$fieldStyleDesc",
                            "$isTitleHidden": true
                        }]
                    }
                }]
            }, {
                "$category": "block",
                "$id": "$capabilities",
                "$title": "$capabilities",
                "$isHidden": true,
                "$layout": {
                    "$layoutType": "row",
                    "$fitToContent": true,
                    "$items": [{
                        "$bind": "$field$capabilityInsert",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$capabilityDelete",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$capabilityAppend",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$capabilityReorder",
                        "$isEditMode": true
                    }]
                }
            }, {
                "$category": "block",
                "$title": "Metadata",
                "$layout": {
                    "$items": [{
                        "$layoutType": "row",
                        "$items": [{
                            "$layoutType": "stack",
                            "$items": [{
                                "$bind": "$field$isHidden",
                                "$isEditMode": true
                            }, {
                                "$bind": "$field$isDisabled",
                                "$isEditMode": true
                            }, {
                                "$bind": "$field$isMandatory",
                                "$isEditMode": true
                            }, {
                                "$bind": "$field$isEditMode",
                                "$isEditMode": true
                            }, {
                                "$bind": "$field$isReadOnly",
                                "$isEditMode": true
                            }]
                        }, {
                            "$layoutType": "stack",
                            "$items": [{
                                "$bind": "$field$description",
                                "$isEditMode": true
                            }, {
                                "$bind": "$field$help",
                                "$isEditMode": true
                            }, {
                                "$bind": "$field$isTopLabelAlignment",
                                "$isEditMode": true
                            }, {
                                "$bind": "$field$isTitleHidden",
                                "$isEditMode": true
                            }, {
                                "$bind": "$field$isRightTextLabelAlignment",
                                "$isEditMode": true
                            }]
                        }],
                        "$fitToContent": true
                    }, {
                        "$bind": "$field$type",
                        "$isEditMode": true
                    }]
                }
            }, {
                "$layoutType": "row",
                "$fitToContent": true,
                "$items": [{
                    "$bind": "$field$LinksTypeMetaDataOnly",
                    "$isEditMode": true
                }, {
                    "$bind": "$field$LinksType",
                    "$isEditMode": true
                }]
            }, {
                "$category": "block",
                "$title": "$diagnoses",
                "$layout": {
                    "$items": [{
                        "$bind": "$field$isDiagnoses",
                        "$choiceLayout": "row",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$isDiagnosesPanelHidden",
                        "$isEditMode": true
                    }]
                }
            }, {
                "$category": "block",
                "$title": "$actions",
                "$layout": {
                    "$layoutType": "row",
                    "$items": [{
                        "$category": "link",
                        "$bind": "$deleteItem",
                        "$isEditMode": true
                    }, {
                        "$category": "link",
                        "$bind": "$insertItem",
                        "$isEditMode": true
                    }, {
                        "$category": "link",
                        "$bind": "$reorderItem",
                        "$isEditMode": true
                    }]
                }
            }, {
                "$category": "block",
                "$title": "Authoring",
                "$id": "authoringTest",
                "$layout": {
                    "$items": []
                }
            }]
        }
    }, {
        "$category": "section",
        "$id": "editSection",
        "$title": "Edit",
        "$layout": {
            "$layoutType": "columns",
            "$layoutSubType": "40,60",
            "$items": [{
                "$layoutType": "stack",
                "$items": [{
                    "$bind": "$field$PartialDelta",
                    "$isEditMode": true,
                    "$format": "$radios",
                    "$choiceLayout": 1
                }, {
                    "$bind": "$field$DefaultDeltaMode",
                    "$isEditMode": true,
                    "$format": "$radios",
                    "$choiceLayout": 1
                }]
            }, {
                "$layoutType": "stack",
                "$items": [{
                    "$bind": "$fieldJsonDelta",
                    "$isEditMode": true,
                    "$rows": 30,
                    "$skin": "s-json-field"
                }]
            }]
        }
    }]

};
