"use strict";
var helpers = require("syracuse-core/lib/helpers");
var MultiArrayTest = require("syracuse-ui/lib/tests/array/multi/multiArrayTest").MultiArrayTest;

function GridCardsArrayTest() {}

exports.GridCardsArrayTest = helpers.defineClass(GridCardsArrayTest, MultiArrayTest, {
	_defineArticle: function($item) {
		var $items = ["inline", "top", "bottom", "left", "popup", "right"].map(function($position) {
			//var $items = ["inline"].map(function($position) {
			//var $items = ["popup"].map(function($position){
			return {
				"$bind": "xarray",
				"$title": "$position=" + $position,
				"$format": "grid",
				"$selectMode": "row",
				"$cardItem": {
					"$position": $position,
					"$layout": {
						"$items": [{
							"$layoutType": "row",
							"$items": [{
								"$bind": "xstring"
							}, {
								"$bind": "xchoice"
							}]
						}, {
							"$layoutType": "row",
							"$items": [{
								"$bind": "xdate"
							}, {
								"$layoutType": "stack",
								"$items": [{
									"$bind": "xboolean"
								}, {
									"$bind": "xdecimal"
								}]
							}]
						}]
					}
				}
			};
		});

		MultiArrayTest.prototype._defineArticle.call(this, {
			"$layoutType": "stack",
			"$items": $items
		});
	}
});