exports.settingBar = {
    "$layoutType": "tabs",
    "$items": [{
        "$category": "section",
        "$title": "Settings",
        "$layout": {
            "$items": [{
                "$category": "block",
                "$id": "$capabilities",
                "$title": "$capabilities",
                "$layout": {
                    "$layoutType": "row",
                    "$fitToContent": true,
                    "$items": [{
                        "$bind": "$field$capabilityInsert",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$capabilityDelete",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$capabilityAppend",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$capabilityReorder",
                        "$isEditMode": true
                    }]
                }
            }, {
                "$category": "block",
                "$title": "Fill",
                "$layout": {
                    "$layoutType": "stack",
                    "$items": [{
                        "$layoutType": "row",
                        "$items": [{
                            "$skin": "s-record-menus-link",
                            "$category": "link",
                            "$bind": "$empty"
                        }, {
                            "$skin": "s-record-menus-link",
                            "$category": "link",
                            "$bind": "$refresh"
                        }]
                    }, {
                        "$layoutType": "row",
                        "$items": [{
                            "$bind": "$fieldRecordCount",
                            "$isEditMode": true
                        }, {
                            "$bind": "$field$fixedBodyHeight",
                            "$isEditMode": true
                        }, {
                            "$bind": "$field$fitContainer",
                            "$isEditMode": true
                        }]
                    }, {
                        "$layoutType": "row",
                        "$items": [{
                            "$bind": "$field$editMode",
                            "$format": "$combo",
                            "$isEditMode": true
                        }]
                    }]
                }
            }, {
                "$category": "block",
                "$title": "Metadata",
                "$layout": {
                    "$items": [{
                        "$bind": "$field$isEditMode",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$description",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$help",
                        "$isEditMode": true
                    }, {
                        "$bind": "$field$isTitleHidden",
                        "$isEditMode": true
                    }]
                }
            }, {
                "$bind": "$field$isDiagnosesPanelHidden",
                "$isEditMode": true
            
            }, {
                "$category": "block",
                "$title": "Authoring",
                "$id": "authoringTest",
                "$layout": {
                    "$items": []
                }
            }]
        }
    }, {
        "$category": "section",
        "$id": "editSection",
        "$title": "Edit",
        "$layout": {
            "$layoutType": "tabs",
            "$items": [{
                "$category": "block",
                $title: "isDeltaMode",
                "$layout": {
                    "$items": [{
                        "$bind": "$field$DefaultDeltaMode",
                        "$isEditMode": true,
                        "$format": "$radios",
                        "$choiceLayout": 1
                    }, {
                        "$category": "links",
                        "$id": "$defaultDeltaModeLinks",
                        "$skin": "s-record-menus"
                    }]
                }
            }, {
                "$category": "block",
                $title: "isPartialDelta",
                "$layout": {
                    "$items": [{
                        "$bind": "$field$PartialDelta",
                        "$isEditMode": true,
                        "$format": "$radios",
                        "$choiceLayout": 1
                    }, {
                        "$category": "links",
                        "$id": "$partialDeltaLinks",
                        "$skin": "s-record-menus"
                    }]
                }
            }]
        }
    }, {
        "$category": "section",
        "$title": "Json Delta",
        "$layout": {
            "$items": [{
                "$bind": "$fieldJsonDelta",
                "$isTitleHidden": true,
                "$isEditMode": true,
                "$rows": 30,
                "$skin": "s-json-field"
            }]
        }
    }]

};
