"use strict";
var helpers = require("syracuse-core/lib/helpers")
var TestPage = require("syracuse-ui/lib/tests/testPage").TestPage;

function MessageBoxTest(){
}

exports.MessageBoxTest = helpers.defineClass(MessageBoxTest, TestPage, {
    _initiliazeTestPage: function(){
        var self = this;
        self.messageTypes = {
            alert: {
                $title: "This is an alert",
                $message: "Hi, I am an alert message\n with carriage return"
            },
            fatal: {
                $autoClose: true,
                $title: "This is a fatal message",
                $message: "Hi, I am a fatal  message",
                $type: "fatal"
            },
            warning: {
                $title: "This is a warning",
                $message: "Hi, I am a warning message",
                $type: "warning"
            },
            info: {
                $autoClose: 1000,
                $title: "This is an information",
                $message: "Hi, I am an information message",
                $type: "info"
            },
            question: {
                $title: "This is a question",
                $message: "Hi, I am a question",
                $type: "question",
                $buttons: "yesnocancel",
                callback: function(response){
                    alert(response.$id);
                }
            },
            prompt: {
                $title: "This is a prompt",
                $message: "Hi, I am a prompt",
                $type: "prompt",
                callback: function(response){
                    alert(response.prompt);
                    return true;
                }
            }
        };
        Object.keys(self.messageTypes).forEach(function($type){
            self.$prototype.$links[$type] = {
                $title: "Open popup"
            }
        });
        self.$item = {
            $layout: {
                $items: [{
                    $layoutType: "columns",
                    $layoutSubType: "50,50",
                    $items: [{
                        $layoutType: "stack",
                        $items: self._makeSection(["alert", "fatal", "warning"])
                    }, {
                        $layoutType: "stack",
                        $items: self._makeSection(["info", "question", "prompt"])
                    }]
                }]
            }
        };
    },
    loadBox: function(){
        var self = this;
        TestPage.prototype.loadBox.call(self);
        Object.keys(self.messageTypes).forEach(function($type){
            var section = self.idMap["messageBoxSection-" + $type];
            if ($type == "info") {
                section.domTitle.textContent += " ($autoClose = 1000)";
            }
            if ($type == "fatal") {
                section.domTitle.textContent += " ($autoClose = true 4s)";
            }
            var $msg = self.messageTypes[$type];
            $msg.$$container = section.$$body;
            document.site.showMessage($msg);
            delete $msg.$$container;
        });
    },
    _makeSection: function($types){
        return $types.map(function($type){
            return {
                $category: "section",
                $id: "messageBoxSection-" + $type,
                $title: "$type = '" + $type + "'",
                $layout: {
                    $items: [{
                        $category: "link",
                        $bind: $type
                    }]
                }
            }
        });
    },
    onMenuClick: function(menu){
        var message = this.messageTypes[menu.$bind];
        if (message) {
            document.site.showMessage(message);
            return false;
        }
        return true;
    }
});
