"use strict";

exports.load = function(page, finish) {
	page.setTitle("xa1-color-picker");
	page.$prototype.$properties = {
		"x-xa1-color": {
			"$title": "x-xa1-color",
			"$type": "application/x-xa1-color"
		}
	};
	syra_widgets.ensureMediaTypes(page.$prototype, function() {
		page.addItem(page.layoutContent.domItem, {
			$isEditMode: true,
			"$bind": "x-xa1-color"
		});
		page.applyChange({
			"x-xa1-color": 2
		});
		finish(page);
	});
};