exports.binary = {
	load: function(page) {
		var $props = page.$prototype.$properties;
		$props.embededPDF = {
			$isTitleHidden: true,
			$type: "application/pdf",
			$url: "/syracuse-ui/lib/tests/pages/field/data/binarytest.pdf"
		};
		$props.embededPNG = {
			$isTitleHidden: true,
			$type: "image/png",
			$url: "/syracuse-ui/lib/tests/pages/field/data/binarytest.png"
		};
		page.addFieldBlock(page.leftCol, "PDF", ["embededPDF"]);
		page.addFieldBlock(page.leftCol, "PNG", ["embededPNG"]);

		page.refreshData = function() {
			page.applyChange({
				embededPDF: {},
				embededPNG: {}
			});
		};
	}
};


exports.icon = {
	load: function(page) {
		page.isAssertSummaryMode = true;
		var $props = page.$prototype.$properties;
		var cols = [];
		for (var ii = 0; ii < 8; ii++) {
			var col = page.addCol(page.leftCol);
			col.style.width = "12%";
			cols.push(col);
		}
		for (var ii = 0, colIndex = 0; ii <= 350; ii++) {
			$props["icon-" + ii] = {
				$title: ii + "",
				$type: "application/x-icon"
			};
			page.loadNewItem(cols[colIndex], {
				$bind: "icon-" + ii,
				$isTitleHidden: false,
				$isEditMode: true
			}).onInputClick = function(input, event) {
				alert(page.currentValue);
			};
			page.loadNewItem(cols[4 + colIndex], {
				$bind: "icon-" + ii,
				$isTitleHidden: false,
				$isEditMode: false
			}).onInputClick = function(input, event) {
				alert(page.currentValue);
			};
			if ((++colIndex) == 4) {
				colIndex = 0;
			}
		}

		page.refreshData = function() {
			var data = {};
			for (var ii = 0, colIndex = 0; ii <= 350; ii++) {
				data["icon-" + ii] = ii;
			}
			page.applyChange(data);
		};
	}
};



exports.editor = {
	load: function(page) {
		var $props = page.$prototype.$properties;
		$props.textrtf = {
			$title: "text-rtf",
			$type: "text/rtf",
			$maxLength: 500,
			$capabilities: "sort,filter,raw",
		};
		$props.texthtml = {
			$title: "text-html",
			$type: "text/html"
		};
		$props.textplain = {
			$title: "text-plain",
			$type: "text/plain",
		};
		$props.textplainRTF = {
			$title: "text-plain",
			$type: "text/plain",
		};
		page.addFieldBlock(page.leftCol, "textplain", ["textplain"]);
		var box = page.addBox("textrtf", "h2");
		var slot = page.leftCol.appendChild(box.slot);
		page.loadNewItem(page.addCol(box.body, "50%"), {
			$bind: "textrtf",
			$rows: 5,
			$isEditMode: true
		});
		var right = page.addCol(box.body, "50%");
		page.loadNewItem(right, {
			$bind: "textrtf"
		});
		page.loadNewItem(right, {
			$bind: "textplainRTF",
			$isEditMode: true
		});
		page.addFieldBlock(page.leftCol, "texthtml", ["texthtml"], function($edit) {
			$edit.$rows = 5;
		});

		page.refreshData = function() {
			page.applyChange({
				textplain: "Hello World!!!",
				textrtf: "{\\rtf1\\ansi{\\fonttbl{\\f0 Bitstream Charter;}} {\\f0 \\fs24 Ceci est un texte accentu\\u233 }\\par \\par {\\f0 des \\fs18 petits}{\\f0  et des \\fs32 gros}\\f0 .}",
				texthtml: "<h1>Hi</h1><p>This is very minimal \"hello world\" HTML document.</p>"
			});
		};

	}
};