"use strict";
var helpers = require('syracuse-core').helpers;
var DesktopPage = require("syracuse-ui/lib/page/desktopPage").DesktopPage;
var _map = require("syracuse-ui/lib/tests/pages/map").map;

function _alertSummary(page, show) {
	if (!page.unitDiagnosesSlot) {
		page.unitDiagnosesSlot = syra_dom.div("s-uitest-unit-diags");
		page.rightCol.insertBefore(page.unitDiagnosesSlot, page.rightCol.firstChild);
	}
	if (show !== false) {
		syra_dom.empty(page.unitDiagnosesSlot);
		var summary = {
			success: {
				count: 0
			},
			error: {
				count: 0
			}
		};
		page.units.forEach(function(unit) {
			if (unit.assertResult) {
				unit.assertResult.forEach(function(assert) {
					if (assert) {
						if (assert.success) {
							summary.success.count++;
						} else {
							summary.error.count++;
						}
					}
				});
			}
		});
		if (summary.success.count) {
			syra_button.add({
				isIndicator: true,
				parent: page,
				slot: page.unitDiagnosesSlot,
				text: summary.success.count + " success",
				css: "s-diag-info s-diag-field",
				fontIcon: "info"
			});
		}
		if (summary.error.count) {
			syra_button.add({
				isIndicator: true,
				parent: page,
				slot: page.unitDiagnosesSlot,
				text: summary.error.count + " error(s)",
				css: "s-diag-error s-diag-field",
				fontIcon: "diagnose"
			});
		}
	}
	syra_dom.hide(page.unitDiagnosesSlot, show === false);
}

function _onUnitClick() {
	var page = this.parent;
	var unit = page.units[this.unitIndex];
	_alertSummary(page, false);
	setTimeout(function() {
		page.runUnit(unit);
		_alertSummary(page);
	}, 10);
}

function _addUnitTests(page, scenarios) {
	page.runUnits = function() {
		var self = this;
		self.units.forEach(function(unit) {
			try {
				self.runUnit(unit);
			} catch (error) {
				unit.assertResult = unit.assertResult || [];
				unit.assertResult.push({
					success: false,
					message: error.message + " " + error.stack
				});
			}
		});
		_alertSummary(self);
		return self.units;
	};
	page.runUnit = function(unit) {
		console.group(unit.$title);
		unit.reset();
		unit.run(this);
		console.groupEnd();
	};


	page.units = [];
	var blockCss = "s-uitest-menus",
		menuCss = "s-uitest-unit-mn";
	if (page.$menuOrientation == "horizontal") {
		blockCss += "-horiz";
		menuCss += "-horiz";
	}


	page.refreshData &&
		syra_button.add({
			parent: page,
			slot: page.rightCol,
			text: "Refresh",
			css: menuCss + "-horiz",
			click: function() {
				this.parent.refreshData();
			}
		});

	syra_button.add({
		parent: page,
		slot: page.rightCol,
		text: "Reset Units",
		css: menuCss + "-horiz",
		click: function() {
			_alertSummary(this.parent, false);
			this.parent.units.forEach(function(unit) {
				unit.reset();
			});
		}
	});
	syra_button.add({
		parent: page,
		slot: page.rightCol,
		text: "Run Unit",
		css: menuCss + "-horiz",
		click: function() {
			this.parent.runUnits();
		}
	});

	var names = scenarios && Object.keys(scenarios);
	if (names && names.length) {
		names.forEach(function($name) {
			var block = syra_dom.div(blockCss);
			var scenario = scenarios[$name]();
			scenario.forEach(function(unit) {
				unit.page = page;
				unit.$title = unit.$title || $name;
				unit.button = syra_button.add({
					parent: page,
					slot: block,
					text: unit.$title || "",
					css: menuCss,
					click: _onUnitClick,
					unitIndex: page.units.length
				});
				unit.applyChange = function(delta) {
					if (delta.$isPartialDelta) {
						page.isFusionPage = true;
					}
					//console.log("%c" + JSON.stringify(delta, null, 2), "color:blue;");
					page.refreshData();
					page.$isEditMode = true; //ensure delta
					page.applyChange(delta);
					page.$isEditMode = false;
					delete page.isFusionPage;
				};
				unit.reset = function() {
					delete this.assertResult;
					if (this._diagnosesSlot) {
						syra_dom.empty(this._diagnosesSlot);
						this._diagnosesSlot.style.display = "none";
					}
				};
				unit.assert = function(success, message) {
					this.assertResult = this.assertResult || [];
					this.assertResult.push({
						success: success,
						message: message = (message || this.$title)
					});
					var $severity = success ? "info" : "error";
					if (!this._diagnosesSlot) {
						this._diagnosesSlot = syra_dom.div("s-uitest-unit-diags");
						this.button.link.parentNode.insertBefore(this._diagnosesSlot, this.button.link.nextSibling);
					}
					this._diagnosesSlot.style.display = "";
					syra_button.add({
						isIndicator: true,
						parent: this,
						slot: this._diagnosesSlot,
						text: message,
						css: "s-diag-" + $severity + " s-diag-field",
						fontIcon: $severity == "info" ? $severity : "diagnose"
					});
				};
				page.units.push(unit);
			});
			page.rightCol.appendChild(block);
		});
	}
};

function _addMenus(page, $menus, rootSlot) {
	for (var ii = 0, jj = $menus.length; ii < jj; ii++) {
		var $menu = $menus[ii];
		if ($menu.$menus) {
			var box = page.addBox($menu.$title, "mn");
			rootSlot.appendChild(box.slot);
			$menu.$menus && _addMenus(page, $menu.$menus, box.slot);
		} else {
			if ($menu.$url) {
				var btn = syra_button.add({
					parent: page,
					slot: rootSlot,
					text: $menu.$title,
					css: "s-uitest-mn",
					click: function(event) {
						syra_router.executeMenu({
							$url: this.link.href
						}, this.parent);
						event.preventDefault();
						event.stopPropagation();
					}
				});
				btn.link.href = $menu.$url;
			}
		}
	}
}

function TestPage() {}

exports.TestPage = helpers.defineClass(TestPage, DesktopPage, {
	addBox: function($title, css) {
		var box = {
			slot: syra_dom.div("s-uitest-group-" + css),
			body: syra_dom.div("s-uitest-group-body-" + css),
		};
		if ($title) {
			box.head = document.createElement("h2");
			box.head.className = "s-uitest-group-head-" + css;
			box.slot.appendChild(box.head).textContent = $title;
		}
		box.slot.appendChild(box.body);
		return box;
	},
	addCol: function(slot, width) {
		var col = syra_dom.div("s-uitest-col", slot);
		if (width) {
			col.style.width = width;
		}
		return col;
	},
	addFieldBlock: function(root, $title, $binds, callback) {
		var box = this.addBox($title, "h2");
		root.appendChild(box.slot);
		var left = this.addCol(box.body, "50%");
		var right = this.addCol(box.body, "50%");
		for (var ii = 0, jj = $binds.length; ii < jj; ii++) {
			var $edit = {
				$bind: $binds[ii],
				$isEditMode: true
			};
			var $read = {
				$bind: $binds[ii]
			};
			callback && callback($edit, $read);
			this.addItem(left, $edit);
			this.addItem(right, $read);
		}
	},
	load: function(initData) {

		var self = this;
		self.$isTitleHidden = true;
		self.isAutoInsertFieldDisabled = true;
		syra_pageBuilder.initialize(self);
		self.$prototype.$properties = self.$prototype.$properties || {};
		DesktopPage.prototype.load.call(self, initData);

		syra_dom.toggleClass(self.menuBar.body, "s-uitest-menubar-body", true);
		_addMenus(self, _map, self.menuBar.body);

		syra_button.add({
			parent: self,
			slot: self.menuBar.body,
			text: "ui lock",
			css: "s-uitest-mn",
			click: function(event) {
				syra_locker.lock(100, false);
				event.preventDefault();
				event.stopPropagation();
			}
		});
		syra_button.add({
			parent: self,
			slot: self.menuBar.body,
			text: "ui lock long",
			css: "s-uitest-mn",
			click: function(event) {
				syra_locker.lock(100, true);
				event.preventDefault();
				event.stopPropagation();
			}
		});

		self.leftCol = self.addCol(self.layoutContent.domItem, "80%");
		self.rightCol = self.addCol(self.layoutContent.domItem, "20%");
		require.async("syracuse-ui/lib/tests/pages/" + self.$prototype.$testName, function(err, module) {
			if (err && err.message) {
				syra_alert.error(err.message);
			} else {
				self.testCase = self.$prototype.$testCategory ? module[self.$prototype.$testCategory] : module;
				self.testCase.load(self, function() {
					_addUnitTests(self, module.scenarios);
					self.refreshData && self.refreshData();
					self.resizeItem(true);
					syra_quality.page_onAfterLoad(self);
				});
			}
		});

	}
});