exports.getRecords = function(count) {
	var records = [];
	for (var index = 0; index < count; index++) {
		records.push({
			"$uuid": "record" + index,
			"$etag": 1,
			"$properties": {},
			"$index": index,
			"xstring": "record " + index,
			"xchoice": index,
			"xdate": "2011-0" + Math.max(Math.min(1, index), 9) + "-07",
			"xboolean": true,
			"xdecimal": 45454.0 * index,
			"xreference": {
				"$uuid": "4e54a41e-5c7d-4926-ad57-6a5f882c24c4",
				"$key": "4e54a41e-5c7d-4926-ad57-6a5f882c24c4",
				"code": "AD",
				"description": "Andorradfd fsdqfsqdfsdq sdqf sdqfsdqf sdqqfsdq fsdqf sdqf sdfsdqf sdf sqdfsd qfsdq fsdqf sdqfsd qfsdq fsqd fs q fqsf",
				"$title": "title"
			}
		});
	}
	return records;
};

