"use strict";
var helpers = require("syracuse-core/lib/helpers");
var DesktopPage = require("syracuse-ui/lib/page/desktopPage").DesktopPage;
var resources = require("./resources");

function TestPage() {}

exports.TestPage = helpers.defineClass(TestPage, DesktopPage, {
	notifyActionChange: function(target, value) {
		if (value.$openSpy) {
			document.site.openSpy();
			return false;
		}
		DesktopPage.prototype.notifyActionChange.call(this, target, value);
	},
	getResources: function() {
		return resources;
	},
	_initiliazeTestPage: function() {

	},
	loadBox: function(initData) {
		this.localize = document.site.localize;
		this._initializePage();
		this._initiliazeTestPage();
		this.$prototype.$properties = this.$prototype.$properties || {};
		DesktopPage.prototype.loadBox.call(this, initData);
	},
	onMenuClick: function(menuItem) {
		if (menuItem.$bind.indexOf("$localize") != -1) {
			// TODO
			return true;
		}
		return true;
	}
});