exports.openPage = function(openerUrlSegments) {
	syra_site.loadStyleSheet("uitest.css");
	syra_site.uiTestController = this;
	require.async("syracuse-ui/lib/tests/testPage", function(err, module) {
		try {
			var $itemPage = {
				layoutSlot: document.createElement("div"),
				$category: "page",
				openerUrlSegments: openerUrlSegments,
				$pageCategoryClass: module.TestPage,
				$representation: {
					$prototype: {
						$testName: openerUrlSegments.params.name.replace("_", "/"),
						$testCategory: openerUrlSegments.params.category,
						$properties: {},
						$links: {},
						$actions: {}
					},
					$article: {}
				}
			};
			$itemPage.openerUrlSegments.fullUrl = document.location.href;
			syra_site.onMainPageChange($itemPage);
		} catch (error) {
			console.error(error.message + "\n" + error.stack);
		}
	});

};