asyncTest("CORS request with callbacks", function() {
	// test("CORS request", function() {

	// var httpQuery = http.parseUrl(corsOrg),
	var httpQuery = http.parseUrl("https://github.com/"),
		failed;

	httpQuery.sendCorsRequest({
		onreadystatechange: function(e) {
			console.log("onreadystatechange");
			// logXhr(this);
		},

		onloadstart: function(e) {
			console.log("onloadstart");
			// logXhr(this);
		},

		onprogress: function(e) {
			console.log("onprogress");
			// logXhr(this);
		},

		onabort: function(e) {
			console.log("onabort");
			// logXhr(this);
			failed = true;
			// start();
		},

		onerror: function(e) {
			console.log("onerror: status=" + this.status);
			// logXhr(this);
			failed = this.status && this.status != 200;
			// start();
		},

		onload: function(e) {
			console.log("onload");
			// logXhr(this);
		},

		ontimeout: function(e) {
			console.log("ontimeout");
			failed = true;
			// logXhr(this);
		},

		onloadend: function(e) {
			console.log("onloadend");
			// logXhr(this);
			ok(!failed, "CORS request succeeded");
			start();
		}
	});
});

