"use strict";

exports.contract = {
	application: "syracuse",
	contract: "uml",
	entities: {
		umlClassDiagram: require('./entities/uml-class-diagram').entity,
	},
	representations: {
		umlClassDiagram: require('./representations/uml-class-diagram').representation,
	},
};

exports.extendContract = function(contract) {
	function copy(src, dst) {
		Object.keys(src).forEach(function(name) {
			dst[name] = src[name];
		});
	}
	copy(exports.contract.entities, contract.entities);
	copy(exports.contract.representations, contract.representations);
};