"use strict";
var helpers = require('syracuse-core').helpers;

exports.CvgWsException = helpers.defineClass(function(message, e) {
	Error.call(this);
	Error.captureStackTrace(this, this); //super helper method to include stack trace in error object
	// Error could be a strings, number...
	if (e && typeof e != "object") e = {
		message: e.toString()
	};
	this.message = e && e.message ? e.message : (message ? message : ""); //set the error message
	this.inner = e;
	this.name = 'CvgWsException'; //set our function’s name as error name.
}, Error, {});
exports.CvgWsCriticalException = helpers.defineClass(function(message, e) {
	exports.CvgWsException.call(this, message, e);
	this.name = 'CvgWsCriticalException'; //set our function’s name as error name.
}, exports.CvgWsException, {});