"use strict";
var helpers = require('@sage/syracuse-core').helpers;
var CvgAskUiAction = require('./CvgAskUiAction').CvgAskUiAction;

var CvgAskOpenFile = helpers.defineClass(function(aUrl) {
	CvgAskOpenFile.call(this);
	//addOneParam("url", aUrl);
}, CvgAskUiAction, {});

exports.CvgAskOpenFile = CvgAskOpenFile;

/**
 * Implementation d'une liste de demandes addressees aux browser d'ouverture
 * d'urls.* Utilise par les fonctions d'export.
 */
var CvgAskOpenFiles = helpers.defineClass(function() {
	this.list = [];
}, null, {
	addOneOpenFile: function(aUrl) {
		var wCallUiOpenFile = new CvgAskOpenFile(aUrl);
		this.list.push(wCallUiOpenFile);
		return wCallUiOpenFile;
	},
	traverseValues: function(aTraverser) {
		var wMax = this.list.length;
		var wI = 0;
		var wEntry;
		while (wI < wMax) {
			wEntry = this.list[wI];
			if (aTraverser.doWhenTraverse(wEntry, wI) === true) {
				return true;
			}
			wI++;
		}
		return false;
	}
});

exports.CvgAskOpenFiles = CvgAskOpenFiles;