"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgObservable = require('syracuse-x3/lib/convergence/model/CvgObservable').CvgObservable;

var CvgCallUiOpenFile = exports.CvgCallUiOpenFile = helpers.defineClass(function(client,type,url) {
	CvgObservable.call(this,client);
	this.url = url;
	
	this.type = type;
	this.setJsonType('MISC');
}, CvgObservable,{
	toJSON: function() {
		return {
			openf : {
				target : [this.type,this.url]
			}
		}
	},
	toJsonObj: function() {
		return toJSON();
	}

});
