"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgObservable = require('syracuse-x3/lib/convergence/model/CvgObservable').CvgObservable;

var CvgCallUiOpenFile = exports.CvgCallUiOpenFile = helpers.defineClass(function(client, type, url, appSrvShared, options) {
	CvgObservable.call(this, client);
	this.targetlist = [
		[type, url, appSrvShared, options]
	];

	this.setJsonType('MISC');
}, CvgObservable, {
	addOpenFile: function(type, url, appSrvShared, options) {
		this.targetlist.push([type, url, appSrvShared, options]);
	},
	toJSON: function() {
		return {
			openf: {
				target: this.targetlist
			}
		};
	},
	toJsonObj: function() {
		return this.toJSON();
	}

});