"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgObservable = require('syracuse-x3/lib/convergence/model/CvgObservable').CvgObservable;

var CvgImpConfig = exports.CvgImpConfig = helpers.defineClass(function(client, records) {
	CvgObservable.call(this, client);
	this.records = records;
	this.setJsonType('TARGET');
}, CvgObservable, {
	toJSON: function() {
		return {
			type: "selpr",
			selpr: {
				$facet: "configPrinter",
				$rcd: this.records
			}
		};
	},
});

var CvgImpMore = exports.CvgImpMore = helpers.defineClass(function(client, infos) {
	CvgObservable.call(this, client);
	this.bac = infos.bac;
	this.paperSize = infos.paperSize;
	this.setJsonType('TARGET');
}, CvgObservable, {

	toJSON: function() {
		return {
			type: "selpr",
			selpr: {
				$facet: "morePrinter",
				bac: this.bac,
				paperSize: this.paperSize
			}
		};
	},

});