"use strict";
var helpers = require('syracuse-core').helpers;

var CvgInstances = exports.CvgInstances = helpers.defineClass(function() {
	this.list = [];
}, null, {
	add: function(a) {
		if (a != null) {
			this.list.push(a);
		}
	},
	traverseChilds: function(aTraverser) {
		var wMax = this.list.length;
		var wI = 0;
		var wEntry;
		while (wI < wMax) {
			wEntry = this.list[wI];
			if (aTraverser.doWhenTraverse(wEntry, wI) === true) {
				return true;
			}
			wI++;
		}
		return false;
	}

});