"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgListOfEntity = require('syracuse-x3/lib/convergence/model/CvgListOfEntity').CvgListOfEntity;

var util = require('util');
var CvgListOfDataSrcs = exports.CvgListOfDataSrcs = helpers.defineClass(function(aRequesterWindow, aScreen) {
	CvgListOfEntity.call(this, aRequesterWindow);
	this.screen = aScreen;
}, CvgListOfEntity, {
	traverseAll: function(aTraverser) {
		var wMax = this.length;
		var wDataSrc;
		var wI = 0;
		while (wI < wMax) {
			wDataSrc = this.get(wI);
			if (aTraverser.doWhenTraverse(wDataSrc, wI) == true) {
				return true;
			}
			if (wDataSrc.traverseAll(aTraverser) == true) {
				return true;
			}
			wI++;
		}
		return false;
	},
	traverseChilds: function(aTraverser) {
		var wMax = this.length;
		var wEntry;
		var wI = 0;
		while (wI < wMax) {
			wEntry = this.get(wI);

			if (aTraverser.doWhenTraverse(wEntry, wI) == true) {
				return true;
			}
			wI++;
		}
		return false;
	},
	traverseSettable: function(aStructIst, aSetter) {
		var wI = aStructIst.blocZeroBase;
		if (wI < 0 || wI >= this.length) {
			var wMess = this.getClient().getMessage('ERROR_DSRC_UNKNOWN', wI.toString(), this.length.toString(), this.screen != null ? this.screen.getAdxId() : "???");
			aSetter.setLogicalErrMess(wMess);
			return false;
		}
		var wDataSrc = this.get(wI);
		return wDataSrc.traverseSettable(aStructIst, aSetter);
	}
});