"use strict";
var helpers = require('syracuse-core').helpers;
var util = require('util');

var CvgListOfEntity = exports.CvgListOfEntity = helpers.defineClass(function(aRequesterWindow) {
	Array.call(this);
	this.requesterWindow = aRequesterWindow;
	this.listedThings = {};
}, Array, {
	get: function(aId) {
		//console.log("GET listedThing element="+util.inspect(this.listedThings));
		//console.log("Test: "+Object.keys(this.listedThings)[0]);
		return this.listedThings[Object.keys(this.listedThings)[aId]];
	},
	getClient: function() {
		return this.requesterWindow.client;
	},
	pushEntity: function(aId, aEntity) {
		this.push(aEntity);
		this.listedThings[aId] = aEntity;
	},
	size: function() {
		return Object.keys(this.listedThings).length;
	},
});