"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgResu = require('syracuse-x3/lib/convergence/types/CvgResu').CvgResu;
var CvgResuTdo = require('syracuse-x3/lib/convergence/types/CvgResu').TDO;
var CvgScreen = require('syracuse-x3/lib/convergence/model/CvgScreen').CvgScreen;
var CvgLeftList = require('syracuse-x3/lib/convergence/model/CvgLeftList').CvgLeftList;
var CvgLeftListLine = require('syracuse-x3/lib/convergence/model/CvgLeftListLine').CvgLeftListLine;
var CvgLeftListLineLevel = require('syracuse-x3/lib/convergence/model/CvgLeftListLine').CvgLeftListLineLevel;
var CvgVariableFactory = require('syracuse-x3/lib/convergence/model/CvgVariableFactory').CvgVariableFactory;
var CvgLeftListRecursiv = require('syracuse-x3/lib/convergence/model/CvgLeftListRecursiv').CvgLeftListRecursiv;
var CvgLeftListHierarchic = require('syracuse-x3/lib/convergence/model/CvgLeftListHierarchic').CvgLeftListHierarchic;
var CvgLeftListLineNode = require('syracuse-x3/lib/convergence/model/CvgLeftListLineNode').CvgLeftListLineNode;
var CvgException = require('syracuse-x3/lib/convergence/model/CvgException').CvgException;

var CvgScreenLeftList = exports.CvgScreenLeftList = helpers.defineClass(function(aRequesterWindow, aListOfLeftLists) {
	CvgScreen.call(this, aRequesterWindow, CvgScreen.SCREEN_LEFTLIST_NUM_ZERO_BASED + 1, "_SCRNLEFT", aListOfLeftLists);
}, CvgScreen, {});

function _extactType(aValue) {
	var wPos = aValue.indexOf(',');
	return parseInt(aValue.substring(0, wPos), 10);
}

function _newLeftListEntry(aStackedWindow, aLeftList, aId, aLeftListNode) {
	if (aLeftList.isRecursive()) {
		return _newLeftListLineNode(aStackedWindow, aLeftList, aId, aLeftListNode);
	} else if (aLeftList.isHierarchic()) {
		return _newLeftListLineFlat(aStackedWindow, aLeftList, aId, aLeftListNode);
	} else {
		return _newLeftListLineFlat(aStackedWindow, aLeftList, aId, aLeftListNode);
	}
}

function _newLeftListLineFlat(aStackedWindow, aLeftList, aId, aLeftListNode) {
	var wNbField = parseInt(aLeftListNode.$.NBZ, 10);
	var wLeftListLine = new CvgLeftListLine(aStackedWindow, aLeftList, aId, wNbField, aLeftList.lines.length);
	var wListOfX3Variables = aLeftListNode.FLD;
	var wForcedTDO = 0;
	_newLeftListLineVars(aStackedWindow, wLeftListLine, aId, wListOfX3Variables, wForcedTDO);
	return wLeftListLine;
}

function _newLeftListLineFlat(aStackedWindow, aLeftList, aId, aLeftListNode) {
	var wNbField = parseInt(aLeftListNode.$.NBZ, 10);
	var wLeftListLine = new CvgLeftListLineLevel(aStackedWindow, aLeftList, aId, wNbField, aLeftList.lines.length);
	var wListOfX3Variables = aLeftListNode.FLD;
	var wForcedTDO = CvgResuTdo["STRU"];
	_newLeftListLineVars(aStackedWindow, wLeftListLine, aId, wListOfX3Variables, wForcedTDO);
	return wLeftListLine;
}

function _newLeftListLineNode(aStackedWindow, aLeftList, aId, aLeftListNode) {
	var wLeftListNode = new CvgLeftListLineNode(aStackedWindow, aLeftList, aId, 1, aLeftList.lines.length);
	var wPrefixId = aLeftList.id;
	wPrefixId = wPrefixId.substring(wPrefixId.length - 1);
	var wVariable = CvgVariableFactory.instanciateLeftListColumn(aStackedWindow, wLeftListNode, 1, wPrefixId + "1");
	wLeftListNode.addOneVar(wVariable);
	return wLeftListNode;
}


function _newLeftListLineVars(aStackedWindow, aLeftListLine, aId, aListOfX3Variables, aForcedTDO) {
	var wVariable;
	var wVariableNode;
	var wI = 0;
	var wMax = Array.isArray(aListOfX3Variables) ? aListOfX3Variables.length : 1;
	if (wMax === 0) {
		var wMess = aStackedWindow.client.getMessage('ERROR_NO_COLUMS', aId);
		throw new CvgException(aStackedWindow.client, null, wMess);
	}
	while (wI < wMax) {
		wVariableNode = wMax === 1 ? aListOfX3Variables : aListOfX3Variables[wI];
		wVariable = CvgVariableFactory.instanciateVariable(aStackedWindow, aLeftListLine, wI + 1, aId, wVariableNode, aForcedTDO);
		aLeftListLine.addOneVar(wVariable);
		wI++;
	}
}

var CvgListOfLeftListsFactory = exports.CvgListOfLeftListsFactory = {
	addLeftLists: function(aStackedWindow, aScreens, aWindowDescription, aListOfLeftLists) {
		var wLeftListNodes = aWindowDescription.LEFTLIST.LST;
		var wMax = Array.isArray(wLeftListNodes) ? wLeftListNodes.length : 1;
		if (wLeftListNodes && wMax > 0) {
			var wScreen = new CvgScreenLeftList(aStackedWindow, aListOfLeftLists);
			aScreens.pushScreenLeftList(wScreen);
			aListOfLeftLists.setScreen(wScreen);
			var wLeftListNode;
			var wLeftList;
			var wLeftListId;
			var wI = 0;
			while (wI < wMax) {
				wLeftListNode = wMax === 1 ? wLeftListNodes : wLeftListNodes[wI];
				wLeftListId = wLeftListNode.$.ID;
				wLeftList = this._newLeftList(aStackedWindow, wScreen, wI + 1, wLeftListId, wLeftListNode);
				aListOfLeftLists.pushEntity(wLeftListId, wLeftList);
				wI++;
			}
		}
	},

	newListEntrySup: function(aStackedWindow, aLeftList, aNbField) {

		var wLeftListLine = new CvgLeftListLine(aStackedWindow, aLeftList, aLeftList.id, aNbField, aLeftList.lines.length);
		var wVariable;
		var wPrefixId = aLeftList.id;
		wPrefixId = wPrefixId.substring(wPrefixId.length - 1);
		var wI = 0;
		while (wI < aNbField) {
			wVariable = CvgVariableFactory.instanciateLeftListColumn(aStackedWindow, wLeftListLine, wI + 1, wPrefixId + (wI + 1).toString());
			wLeftListLine.addOneVar(wVariable);
			wI++;
		}
		return wLeftListLine;
	},

	_newLeftList: function(aStackedWindow, aScreen, aEntityIndexBaseOne, aId, aLeftListNode) {
		var wListFormat = _extactType(aLeftListNode.$.TYL);
		var wNbField = parseInt(aLeftListNode.$.NBZ, 10);
		var wLeftList;

		if (CvgLeftList.isHierarchicFormat(wListFormat)) {
			var wNbKeyParts = parseInt(aLeftListNode.$.NBK, 10);
			wLeftList = new CvgLeftListHierarchic(aStackedWindow, aScreen, aEntityIndexBaseOne, aId, wListFormat, wNbField, wNbKeyParts);
		} else if (CvgLeftList.isRecursiveFormat(wListFormat)) {
			wLeftList = new CvgLeftListRecursiv(aStackedWindow, aScreen, aEntityIndexBaseOne, aId, wListFormat, wNbField);
		} else {
			wLeftList = new CvgLeftList(aStackedWindow, aScreen, aEntityIndexBaseOne, aId, wListFormat, wNbField);
		}
		var wLeftListLine = _newLeftListEntry(aStackedWindow, wLeftList, aId, aLeftListNode);
		wLeftList.setEntrySup(wLeftListLine);
		return wLeftList;
	}
};