"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgAction = require('syracuse-x3/lib/convergence/client/CvgAction').Actions;

var CvgPickedLine = exports.CvgPickedLine = helpers.defineClass(function(aStructIst,key,leftList, aAction) {
	this.ist = aStructIst;
	this.key = key;
	this.action = aAction;
	this.leftList = leftList;
}, null, {
	isSelection: function() {
		console.log("action "+this.action);
		return this.action === CvgAction.SRV_TREESELECTLINE || this.action === CvgAction.SRV_BRWSELECTLINE;
	}
})
