"use strict";
var helpers = require('syracuse-core').helpers;

var tracerStd = require('syracuse-core').getTracer("classic.std");

var CvgPickedLine = exports.CvgPickedLine = helpers.defineClass(function(client, aStructIst, key, leftList, aAction) {
	this.ist = aStructIst;
	this.key = key;
	this.action = aAction;
	this.leftList = leftList;
	this.client = client;
}, null, {
	isSelection: function() {
		tracerStd.info && tracerStd.info((new Date().toISOString()) + " action " + this.action);
		return this.action === this.client.x3writer.descriptors.actions.SRV_TREESELECTLINE || this.action === this.client.x3writer.descriptors.actions.SRV_BRWSELECTLINE;
	}
});