"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgObservable = require('syracuse-x3/lib/convergence/model/CvgObservable').CvgObservable;

var CvgReboot = exports.CvgReboot = helpers.defineClass(function(client) {
	CvgObservable.call(this, client);
	folder = client.adonixContext.x3Folder;
	user = client.adonixContext.x3UserId;
	lang = client.adonixContext.language;
	date = new Date().getTime().toString();
	
	// En dernier
	this.setJsonType('SESSION');
}, CvgObservable, {
	toJSON: function() {
		var close = [];
		close.push(folder);
		close.push(user);
		close.push(lang);
		close.push(date);
		return close;

	},
	toJsonArray: function() {
		return toJSON();
	}
});