"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgObservable = require('syracuse-x3/lib/convergence/model/CvgObservable').CvgObservable;

var CvgSessionOpen = helpers.defineClass(function(client, ctx) {
	CvgObservable.call(this, client);
	this.ctx = ctx;
	this.setJsonType('SESSIONOPEN');
	this.forceChange();
}, CvgObservable, {
	toJSON: function() {
		return {
			ctx: this.ctx
		};
	}
});

// Factory pattern avoids jslint/jshint complaining about using "new" for side effects
exports.create = function(client, ctx) {
	return new CvgSessionOpen(client, ctx);
};