"use strict";
var helpers = require('@sage/syracuse-core').helpers;
var CvgObservable = require('./CvgObservable').CvgObservable;

var CvgTargetSpecial = exports.CvgTargetSpecial = helpers.defineClass(function(name, client) {
	CvgObservable.call(this, client);
	this.name = name;
	// En dernier
	this.setJsonType('TARGET');
}, CvgObservable, {
	toJSON: function() {
		return {
			type: this.name
		};
	}
});