"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgVariableLocalMenu = require('syracuse-x3/lib/convergence/model/CvgVariableLocalMenu').CvgVariableLocalMenu;

var CvgVariableCheck = exports.CvgVariableCheck = helpers.defineClass(function(srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState, aLocalMenu) {
	CvgVariableLocalMenu.call(this, srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState, aLocalMenu);
}, CvgVariableLocalMenu, {
	clone: function() {
		var wVariable = new CvgVariableCheck(this.srcLine, this.entityIndexBaseOne, this.name, this.id, this.dataType, this.format, this.state, this.localMenu);
		wVariable.mustBeFormatted = this.mustBeFormatted;
		this.cloneValues(wVariable);
		return wVariable;
	},

	// 	        server   | adapi
	// 	------------------|----
	// 	not initialized  0 | 0
	// 	false            1 | 0
	// 	true             2 | 1
	formatData: function(aValue) {
		return aValue;
	},
	getDisplayValue: function() {
		return this.formatData(this.getServerValStr())
	},
	unFormatData: function(aValue) {
		return aValue;
	}
});