var CvgVariablePass = exports.CvgVariablePass = helpers.defineClass(function(client, srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState) {
	this.cli = client;
	CvgVariable.call(this, srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState);
}, CvgVariable, {
	clone: function() {
		var wVariable = new CvgVariablePass(this.srcLine, this.entityIndexBaseOne, this.name, this.id, this.dataType, this.format, this.state);
		wVariable.mustBeFormatted = this.mustBeFormatted;
		this.cloneValues(wVariable);
		return wVariable;
	},
	setInitialValue: function(aValue) {
		throw new CvgException(this.cli, null, "Crypton X3Decrypt is not implemented.", null, "Convergence Server");
		//aValue.setStruValue(CCrypton.X3Decrypt(aValue.value));
		//CvgVariable.prototype.storeBrowserValue.call(this, aValue);
	}
});