"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CvgVariableDate = require('syracuse-x3/lib/convergence/model/CvgVariableDate').CvgVariableDate;

var CvgVariablePeriod = exports.CvgVariablePeriod = helpers.defineClass(function(srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState) {
	CvgVariableDate.call(this, srcLine, aEntityIndexBaseOne, aVarName, aID, aTdo, aFormat, aEntityState);
}, CvgVariableDate, {
	callUnFormater: function(aValue) {
		var wEditDate = CvgVariableDate.prototype.callUnFormater.call(this,aValue);
		var val = this.getServerValStr();
		if (val != null && val.length >= 4) return val.substring(0, 4) + wEditDate.substring(4);
		return wEditDate;
	},
	clone: function() {
		var wVariable = new CvgVariablePeriod(this.srcLine, this.entityIndexBaseOne, this.name, this.id, this.dataType, this.format, this.state);
		wVariable.mustBeFormatted = this.mustBeFormatted;
		this.cloneValues(wVariable);
		return wVariable;
	}
});
