"use strict";
var helpers = require('syracuse-core').helpers;

var CvgWindow = exports.CvgWindow = helpers.defineClass(function(aModele, aType, aNbLignes, aNbColonnes) {
	this.modele = aModele;
	this.type = aType;
	this.nbLignes = aNbLignes;
	this.nbColonnes = aNbColonnes;
}, null, {
	setNbColonnes: function(aNbColonnes) {
		var nb = parseInt(aNbColonnes, 10);
		this.nbColonnes = isNaN(nb) ? -1 : nb;
	},
	setNbLignes: function(aNbLignes) {
		var nb = parseInt(aNbLignes, 10);
		this.nbLignes = isNaN(nb) ? -1 : nb;
	}
});

CvgWindow.WIN_MODELE_DIVERSE = 1;
CvgWindow.WIN_TYPE_CLIENTE = 1;
CvgWindow.PREFIX_SPECIAL = '_';