"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = require('syracuse-x3/lib/fusion/AdapiObservable').AdapiObservable;

var AdapiMenuAct = exports.AdapiMenuAct = helpers.defineClass(function(aclient, id, aEnabled) {
	AdapiObservable.call(this, aclient);
	this.id = id;
	this.enabled = aEnabled;
	// var dernier
	setJsonType('ACT');
}, AdapiObservable, {
	getAdapiObservableId: function() {
		return Integer.toString(this.id);
	},
	toJson: function() {
		return {
			id: String.valueOf(this.id),
			st: this.enabled,
		};
	},
	toString: function() {
		return toJson().toString();
	}

});