"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = function() {};

var AdapiMenuCtx = helpers.defineClass(function(client, id, enabled, label) {
	AdapiObservable.call(this, client);
	this.id = id;
	this.enabled = enabled;
	this.label = label;
	// En dernier
	setJsonType('TARGETACT');
}, AdapiObservable, {

	// Vidage des contextButtons sur noaid NDOMNCO
	clear: function() {
		this.cleared = true;
	},
	getAdapiObservableId: function() {
		return Integer.toString(this.id);
	},
	toJson: function() {
		if (this.cleared) {
			return null;
		}
		var object = new JSONArray();
		object.put(String.valueOf(this.id));
		object.put(this.enabled);
		if (this.label != null && !this.label.isEmpty()) {
			object.put(this.label);
		}
		return object;
	},
	toString: function() {
		return toJson().toString();
	}
});