"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = require('syracuse-x3/lib/fusion/AdapiObservable').AdapiObservable;

var AdapiReboot = exports.AdapiReboot = helpers.defineClass(function(client) {
	AdapiObservable.call(this, client);
	folder = client.adonixContext.x3Folder;
	user = client.adonixContext.x3UserId;
	lang = client.adonixContext.language;
	date = new Date().getTime().toString();
	
	// En dernier
	this.setJsonType('SESSION');
}, AdapiObservable, {
	toJSON: function() {
		var close = [];
		close.push(folder);
		close.push(user);
		close.push(lang);
		close.push(date);
		return close;

	},
	toJsonArray: function() {
		return toJSON();
	}
});