"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = function() {};

var AdapiReboot = helpers.defineClass(function(client) {
	AdapiObservable.call(this, client);
	folder = client.adonixContext.x3Folder;
	user = client.adonixContext.x3UserId;
	lang = client.adonixContext.language;
	date = Calendar.instance.time.toString();
	// En dernier
	setJsonType('SESSION');
}, AdapiObservable, {
	toJson: function() {
		var close = new JSONArray();
		close.put(folder);
		close.put(user);
		close.put(lang);
		close.put(date);
		return close;

	},
	toJsonArray: function() {
		return toJson();
	}
});