"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = require('syracuse-x3/lib/fusion/AdapiObservable').AdapiObservable;

var AdapiTargetSpecial = exports.AdapiTargetSpecial = helpers.defineClass(function(name, client) {
	AdapiObservable.call(this, client);
	this.name = name;
	// En dernier
	setJsonType('TARGET');
}, AdapiObservable, {
	toJson: function() {
		var object = new JSONObject();
		object.put('type', name);
		return object;

	}
});