"use strict";
var helpers = require('syracuse-core/lib/helpers');

var CAdapiFormat = exports.CAdapiFormat = helpers.defineClass(function(aFormat) {
	this.pattern = aFormat;
}, null, {
	isValid: function() { return !!this.pattern; },
	isVisible: function() { return true; },
	isLocalMenu: function() { return false; },
	hasFormatChars: function() { return false; },
	setFormatChars: function() { return ""; },
	toString: function() { return "FORMAT: " + this.pattern; }
});

CAdapiFormat.newEmptyFormat = function() {
	return new CAdapiFormat("");
}