"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = require('syracuse-x3/lib/fusion/AdapiObservable').AdapiObservable;
var CAdapiEntity = require('syracuse-x3/lib/fusion/CAdapiEntity').CAdapiEntity;
var CAdapiScreenDummy = require('syracuse-x3/lib/fusion/CAdapiListOfScreens').CAdapiScreenDummy;
var CAdapiVariable = require('syracuse-x3/lib/fusion/CAdapiVariable').CAdapiVariable;
var CAdapiException = require('syracuse-x3/lib/fusion/CAdapiException').CAdapiException;

var EAdapiInstanceKind = {
	TARGET: 0,
	PREVIOUS: 1
};

var CAdapiInstance = exports.CAdapiInstance = helpers.defineClass(function(cli, aKind, aIst, format, aValue, modCmd) {
	AdapiObservable.call(this, cli);
	this.nl = -1;
	this.exception = null;
	if (cli == null) {
		throw new Error("Unexpected null FusionClient");
	}
	this.ist = aIst;
	this.kind = aKind;
	this.rawValue = null;
	this.modCommande = modCmd;
	this.format = format == null || format === "" ? null : format;
	this.value = aValue;
	this.client = cli;
	var adapi = cli.findModifiable(aIst);
	if (adapi != null && adapi instanceof CAdapiEntity) {
		this.entity = adapi;
	}
	if (this.entity == null) {
		throw new Error("Entity " + aIst.toString() + " not found\nPlease check/update X3 Window description");
	}
	// !! En dernier car update du client et this.entity doit être valorisee
	if (aKind == EAdapiInstanceKind.PREVIOUS) {
		this.setJsonType('PREVIOUS');
	} else {
		this.setJsonType('TARGET');
	}
}, AdapiObservable, {
	getNl: function() {
		if (this.nl != -1) {
			return this.nl;
		} else {
			var v = this.entity instanceof CAdapiVariable ? this.entity : null;
			if (v != null && !v.isDisposed()) {
				var line = v.srcLine;
				if (line != null && line.dataSrc.isBlocGrid()) {
					return line.nodeId;
				}
			}
		}
		return -1;
	},
	getXid: function() {
		var fullId = this.ist.screenAlphaIdFusion;
		if (!this.entity.isDisposed() && !(this.entity instanceof CAdapiScreenDummy)) {
			fullId += this.ist.blocAlphaId;
			if (this.entity.entityTyp == 'FIELD') {
				fullId += this.ist.field;
			}
		}
		return fullId;
	},
	hasException: function() {
		return this.exception != null;
	},
	hasFormat: function() {
		return this.format != null;
	},
	setException: function(e) {
		this.exception = e;
	},
	setNl: function(nl) {
		this.nl = nl;
	},
	setRawValue: function(aValue) {
		this.rawValue = aValue;
	},
	toJSON: function() {
		var object = {};
		if (this.ist.win == 1) {
			// target portal
			object.type = 'portal';
		} else {
			object.type = 'ist';
			var ist = {};
			ist.xid = this.getXid();
			if (this.getNl() != -1) {
				ist.nl = this.getNl();

			}
			if (this.format != null) {
				ist.fmt = this.format;
			}
			ist.win = this.ist.winAlphaId;
			ist.edit = !this.modeCommande;
			// Valeur pour champ en mode edit - TODO Vérifier si ok
			if (!this.modeCommande && this.value != null) {
				ist.v = this.value;
				if (this.rawValue != null) ist.raw = this.rawValue;
			}
			object.ist = ist;
			ist.ctrlFailed = this.hasException();
			if (this.hasException()) {
				var e = new CAdapiException(this.client, this.exception, this.exception.message);
				ist.$diagnoses = e.diagnose;
			}
		}
		return object;
	}
});
CAdapiInstance.newprevious = function(aClient, aIst, aFormatString, aValue, modCommande) {
	return new CAdapiInstance(aClient, EAdapiInstanceKind.PREVIOUS, aIst, aFormatString, aValue, modCommande);
}

CAdapiInstance.newtarget = function(aClient, aIst, aFormatString, aValue, modCommande) {
	return new CAdapiInstance(aClient, EAdapiInstanceKind.TARGET, aIst, aFormatString, aValue, modCommande);
}