"use strict";
var helpers = require('syracuse-core/lib/helpers');
var AdapiObservable = function() {};

var CAdapiInstanceExeci = helpers.defineClass(function(xmlAck_call, client, ist) {
	TODO_SUPER(client);
	this.instance = ist;
	this.xml = xmlAck_call;
	this.value = null;
	this.modeCommande = true;
	// En dernier
	setJsonType('TARGET');
}, AdapiObservable, {
	format: {
		get: function() {
			return this.instance == null ? "" : this.instance.format;
		}
	},
	getNl: function() {
		return 0;
	},
	getXid: function() {
		return this.instance == null ? "" : this.instance.ist.blocAlphaId.concat(this.instance.ist.fieldId);
	},
	hasFormat: function() {
		return this.instance == null ? false : this.instance.hasFormat();
	},
	setInstance: function(instance) {
		this.instance = instance;
	},
	setXml: function(xml) {
		this.xml = xml;
	},
	toJson: function() {
		var object = {};
		object.type = 'rpc';
		var rpc = {};
		rpc.xml = this.xml;
		object.put('rpc', rpc);
		var ist = {};
		if (this.instance != null) {
			ist.xid = this.getXid();
			if (this.instance.hasFormat()) {
				ist.fmt = this.format;
			}
			ist.win = this.instance.ist.winAlphaId;
			ist.edit = true;
			rpc.ist = ist;
		}
		return object;
	}
});