"use strict";
var helpers = require('syracuse-core/lib/helpers');

var CAdapiInstances = exports.CAdapiInstances = helpers.defineClass(function() {
	this.list = new ArrayList(CAdapiInstance)();
}, null, {
	add: function(a) {
		if (a != null) {
			this.list.add(a);
		}
	},
	traverseChilds: function(aTraverser) {
		var wMax = this.list.size();
		var wI = 0;
		var wEntry;
		while (wI < wMax) {
			wEntry = this.list.get(wI);
			if (aTraverser.doWhenTraverse(wEntry, wI) == true) {
				return true;
			}
			wI++;
		}
		return false;
	}

});