"use strict";
var helpers = require('syracuse-core/lib/helpers');

var CAdapiInstances = exports.CAdapiInstances = helpers.defineClass(function() {
	this.list = [];
}, null, {
	add: function(a) {
		if (a != null) {
			this.list.push(a);
		}
	},
	traverseChilds: function(aTraverser) {
		var wMax = this.list.length;
		var wI = 0;
		var wEntry;
		while (wI < wMax) {
			wEntry = this.list[wI];
			if (aTraverser.doWhenTraverse(wEntry, wI) == ITraverser.STOP_TRAVERSING) {
				return ITraverser.STOP_TRAVERSING;
			}
			wI++;
		}
		return ITraverser.CONTINUE_TRAVERSING;
	}

});