"use strict";
var helpers = require('syracuse-core/lib/helpers');
var CAdapiLeftListLine = require('syracuse-x3/lib/fusion/CAdapiLeftListLine').CAdapiLeftListLine;


var CAdapiLeftListLineNode = exports.CAdapiLeftListLineNode = helpers.defineClass(function(aStackedWindow, aLeftList, aId, aNbVariables, idx) {
	CAdapiLeftListLine.call(this, aStackedWindow, aLeftList, aId, aNbVariables, idx);
	this.subLeftListNodes = [];
}, CAdapiLeftListLine, {
	attachLefListNode: function(aLeftListNode) {
		this.subLeftListNodes.push(aLeftListNode);
	},
	clone: function() {
		var idx = this.dataSrc.lines.length;
		var wClone = new CAdapiLeftListLineNode(this.requester, this.dataSrc, this.id, this.vars.length, idx);
		cloneVars(wClone);
		return wClone;
	},
	hasSubNode: function() {
		return this.subLeftListNodes.length > 0;
	},
	setColumns: function(aIdx, aText, aClf) {
		setZeroBaseIdx(aIdx);
		setCle(aClf);
		var v = this.getVar(0);
		if (v != null) {
			v.setUnModifiableServerValue(new CStructResu(CStructResu.X3TYP_STRU, aText));
		}
	},
	traverseChilds: function(aTraverser) {
		var wTraversingState = false;
		if (hasSubNode()) {
			wTraversingState = traverseSubNodes(aTraverser);
		}
		if (wTraversingState == false) {
			CAdapiLeftListLine.prototype.traverseChilds.call(this,aTraverser);
		}
		return wTraversingState;
	},
	traverseNodes: function(aTraverser, aWithSubNodes) {

		var wTraversingState = false;
		var wLeftListNode;
		var wMax = this.subLeftListNodes.length;
		var wI = 0;
		while (wI < wMax) {
			wLeftListNode = this.subLeftListNodes[wI];
			wTraversingState = aTraverser.doWhenTraverse(wLeftListNode, wI);
			if (wTraversingState != true && aWithSubNodes && wLeftListNode.hasSubNode()) {
				wTraversingState = wLeftListNode.traverseNodes(aTraverser, aWithSubNodes);
			}
			if (wTraversingState == true) {
				return true;
			}
			wI++;
		}
		return wTraversingState;
	},
	traverseSubNodes: function(aTraverser) {
		return traverseNodes(aTraverser, false);
	}
});